// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

contract r1 {
    address ownerPatient;
    address creatorDoctor;
    bytes recordId;
    mapping(address => bool) canView;
    mapping(address => bool) canCreate;

    constructor(bytes memory _recordId, address _patient, address _doctor) {
        ownerPatient = _patient;
        recordId = _recordId;
        creatorDoctor = _doctor;
        canView[_doctor] = true;
        canView[_patient] = true;
    }

    function giveViewPermission(address viewer) public {
        require(
            msg.sender == ownerPatient,
            "Only patient can give permission!!"
        );
        canView[viewer] = true;
    }

    function revokeViewPermission(address viewer) public {
        require(
            msg.sender == ownerPatient,
            "Only patient can revoke permission!!"
        );
        require(canView[viewer] == true, "No need to revoke!!");
        canView[viewer] = false;
    }

    function viewrecord() public view returns (bytes memory, address, address) {
        require(
            canView[msg.sender] == true,
            "You Dont have the permission to view"
        );
        return (recordId, ownerPatient, creatorDoctor);
    }
}
